import { useEffect } from "react";

export default function Timer({ dispatch, secondsRemaining }) {
  useEffect(() => {
    setInterval(() => {
      if (secondsRemaining === 0) {
        dispatch({ type: "finished" });
      }
    }, 1000);
  }, []);
  return <div className="timer">{secondsRemaining}</div>;
}
